import React, {useEffect,useState, useContext, useRef} from 'react';
import { FirebaseContext } from '../../firebase';
import ReservaDetalle from './ReservaDetalle';

const Reserva = ({habitacion}) => {

    
    const existenciaRef = useRef(habitacion.existencia);
    //Context de firebase
    const { firebase } = useContext(FirebaseContext);
    const { email,name, cellphone} = habitacion;
    const [reservas, guardarReservas] = useState([]);
    
    useEffect(() =>{
         
        const obtenerReservas = () => {
            firebase.db.collection('usuarios').doc(email).collection("reservas").where("existencia","==",false).onSnapshot(manejarSnapshot);
        
        }
        obtenerReservas();
    }, []);

    
    function manejarSnapshot(snapshot) {
        const reservas = snapshot.docs.map(doc => {
            return {
               id: doc.id,
               ...doc.data() 
            }
        });
        guardarReservas(reservas);
            
    }
    const actualizarReserva = () => {
        const existencia = (existenciaRef.current.value === "true");
        try {
            firebase.db.collection('usuarios')
                .doc(email)
                .update({
                    existencia,
                });
        } catch (error) {
            console.log(error);
        }

    }
    // define el tiempo de entrega en tiempo real
    
        //Completar el estado de una reserva

       // const completarReserva = id => {
        //    try {
          //      firebase.db.collection('ordenes')
        //        .doc(id)
        //        .update({
        //            completado: true
        //        })
        //        
        //    } catch (error) {
        //        console.log(error);
        //        
         //   }
        //}
        const aprobarReserva = (idCliente) =>{
            try{
                firebase.db.collection('usuarios').doc(idCliente).collection('reservas').update({aprobado: true})
            }catch(error){
                console.log(error);
            }
        }
    
        const deleteobject = async (idCliente) => {
                if (window.confirm("Seguro que deseas eliminar esta Reserva? "))
                    await firebase.db.collection('usuarios').doc(idCliente).collection('reservas').where('aprobado', "==", false).delete();
        
        }
        
       
    return (
        <div className="w-full px-3 mb-4">
           <div className="p-5 shadow-md bg-white">
                <div className="lg:flex">
                    <div className="w-full px-3 mb-4">
                       <div className="w-full px-3 mb-4">
                        <p className="font-bold text-2xl text-gray-800 lg-4">Información del cliente
                        </p>
                        <p className="text-gray-600 mb-4">Correo del cliente: {''}
                            <span className="text-gray-700 font-bold"> {email} </span>
                        </p>
                        <p className="text-gray-600 mb-4">Nombre: {''}
                            <span className="text-gray-700 font-bold"> {name} </span>
                        </p>
                        <p className="text-gray-600 mb-4">Telefono: {''}
                            <span className="text-gray-700 font-bold"> {cellphone} </span>
                        </p>
                        <div className="sm:flex sm:flex-wrap -mx-3">
                            {reservas.map(reserva =>(
                            <ReservaDetalle
                                key={reserva.id}
                                habitacion={reserva}
                            />))}
                             <div className="lg:w-7/12 xl:w-9/12 pl-5">
                                    <div class="flex-container" >
                                            <div class="bordesbutton" >
                                                <button onClick={() => deleteobject(email)} className="bg-red-800 hover:bg-blue-700, inline-block mb-5 p-2 text-white uppercase font-bold" > ELIMINAR </button>
                                            </div>
                                            <div class="bordesbutton" >
                                                <button onClick={() => aprobarReserva()}  className=" bg-green-500 hover:bg-blue-700, inline-block mb-5 p-2 text-white uppercase font-bold"> APROBAR </button>
                                            </div>
                                    </div>
                                </div>
                        </div>
                                             
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Reserva;
