import React,{useEffect, useState, useContext}from 'react';
import { FirebaseContext } from '../../firebase';
import Reserva from '../ui/Reserva';

const Reservas = () => {

    // context con las operaciones de firebase

    const {firebase} = useContext(FirebaseContext);
    
    const [usuarios, guardarUsuarios] = useState([]);
    const [reservas, guardarReservas] = useState([]);

    useEffect(() =>{
         
        const obtenerUsuarios = () => {
            firebase.db.collection('usuarios').onSnapshot(manejarSnapshot2);
        }
        obtenerUsuarios();
    }, []);

    function manejarSnapshot2(snapshot) {
        const usuarios = snapshot.docs.map(doc => {
            return {
               id: doc.id,
               ...doc.data() 
            }
            
        });
        console.log(usuarios);
        guardarUsuarios(usuarios);
            
    }

    useEffect(() =>{
         
        const obtenerReservas = () => {
            firebase.db.collection('usuarios').doc('prueba1@mail.com').collection('reservas').onSnapshot(manejarSnapshot);
        
        }
        obtenerReservas();
    }, []);

    
    function manejarSnapshot(snapshot) {
        const reservas = snapshot.docs.map(doc => {
            return {
               id: doc.id,
               ...doc.data() 
            }
        });
        console.log(reservas);
        guardarReservas(reservas);
            
    }
   
    return (
        <>
            <h1 className="text-3xl font-light mb-4">Reservas</h1>
            <h2 className="text-3xl font-light mb-4">Reservas pendientes</h2>
            <div className="sm:flex sm:flex-wrap -mx-3">
                {usuarios.map(usuario =>(
                    <Reserva
                        key={usuario.id}
                        habitacion={usuario}

                    />
                ))}
            </div>
        </>
    );
}

export default Reservas;
