import React, {useState, useContext} from 'react';
import { FirebaseContext } from '../../firebase';

const Reserva = ({reserva}) => {

    const [tiempo, guardarTiempo] = useState(0);
    
    //Context de firebase

    const { firebase } = useContext(FirebaseContext);

    // define el tiempo de entrega en tiempo real

    const definirTiempo = id => {
        try {
          firebase.db.collection('ordenes')
            .doc(id)  
            .update({
                tiempo
            })
        } catch (error) {
            console.log(error);
        }
    }

        //Completar el estado de una reserva

        const completarReserva = id => {
            try {
                firebase.db.collection('ordenes')
                .doc(id)
                .update({
                    completado: true
                })
                
            } catch (error) {
                console.log(error);
                
            }
        }
    
    return (
        <div className="sm:w-1/2 lg:w-1/3 px-2 mb-4">
            <div className="p-3 shadow-md bg-white">
                <div class="flex-container" >
                        <div class="bordesbutton" >
                            <button onClick={() => deleteobject(id)} className="bg-red-800 hover:bg-blue-700, inline-block mb-5 p-2 text-white uppercase font-bold" > ELIMINAR </button>
                        </div>
                        <div class="bordesbutton" >
                            <button onClick={() => deleteobject(id)}  className=" bg-green-500 hover:bg-blue-700, inline-block mb-5 p-2 text-white uppercase font-bold"> APROBAR </button>
                        </div>
                </div>
                
                {!reserva.completado && reserva.tiempo >0 &&  (
                    <button
                    type= "button"
                    className="bg-blue-800 hover:bg-blue-700 w-full mt-5 p-2 text-white uppercase font-bold"
                    onClick={ () => completarReserva( reserva.id )}
                    >
                        Marcar como lista 
                    </button>
                )}

            </div>
        </div>
    );
}

export default Reserva;
