import React, {useState, useContext, useRef} from 'react';
import { FirebaseContext } from '../../firebase';

const Reserva = ({habitacion}) => {

    //const [tiempo, guardarTiempo] = useState(0);
    const existenciaRef = useRef(habitacion.existencia);
    //Context de firebase

    const { firebase } = useContext(FirebaseContext);
    const { idCliente, startDay, endDay ,Imagen, existencia } = habitacion;
    
    const actualizarReserva = () => {
        const existencia = (existenciaRef.current.value === "true");
        try {
            firebase.db.collection('usuarios')
                .doc(idCliente)
                .update({
                    existencia,
                });
        } catch (error) {
            console.log(error);
        }

    }
    // define el tiempo de entrega en tiempo real
    
        //Completar el estado de una reserva

       // const completarReserva = id => {
        //    try {
          //      firebase.db.collection('ordenes')
        //        .doc(id)
        //        .update({
        //            completado: true
        //        })
        //        
        //    } catch (error) {
        //        console.log(error);
        //        
         //   }
        //}
    const aprobarReserva = (idCliente) =>{
        try{
            firebase.db.collection('productos').doc(idCliente).collection('reservas').update({aprobado: true})
        }catch(error){
            console.log(error);
        }
    }
    const deleteobject = async (idCliente) => {
            if (window.confirm("Seguro que deseas eliminar esta habitacion"))
                await firebase.db.collection('productos').doc(idCliente).collection('reservas').where('aprobado', "==", false).delete();
    
    }
    
    
    return (
        <div className="w-full px-3 mb-4">
           <div className="p-5 shadow-md bg-white">
                <div className="lg:flex">
                    <div className="lg:w-5/12 xl:w-3/12">
                        <img src={Imagen} alt="imagen habitacion" />
                        <div className="sm:flex sm:-mx-2 pl-2">
                            <label className="block mt-5 sm:w-2/4">
                                <span className="block text-gray-800 mb-2"> Estado de reserva </span>
                                <select 
                                    className="bg-white shadow appearance-none border rounded w-full py-2 px-3 leading-tight focus:outline-none focus:shadow-outline"
                                    value={existencia}
                                    ref={existenciaRef}
                                    onChange={() => actualizarReserva()}
                                    >
                                    <option value="true">Disponible</option>
                                    <option value="false">No Disponible</option>
                                </select>
                            </label>
                        </div>

                                <div className="lg:w-7/12 xl:w-9/12 pl-5">
                                    <div class="flex-container" >
                                            <div class="bordesbutton" >
                                            
                                                <button onClick={() => deleteobject(idCliente)} className="bg-red-800 hover:bg-blue-700, inline-block mb-5 p-2 text-white uppercase font-bold" > ELIMINAR </button>
                                            </div>
                                            <div class="bordesbutton" >
                                                <button onClick={() => aprobarReserva(idCliente)}  className=" bg-green-500 hover:bg-blue-700, inline-block mb-5 p-2 text-white uppercase font-bold"> APROBAR </button>
                                            </div>
                                    </div>
                                </div>

                    </div>
                </div>
            </div>
        </div>
    );
}

export default Reserva;
